import React, { useEffect, useState } from "react";
import axios from "axios";

const Configurator = () => {
  const [composants, setComposants] = useState([]);
  const [config, setConfig] = useState({
    cpu: null,
    carte_mere: null,
    ram: null,
    gpu: null,
    stockage: null,
    alim: null,
    boitier: null
  });
  const [totalPrix, setTotalPrix] = useState(0);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/composants")
      .then((response) => setComposants(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleSelection = (categorie, id) => {
    const composant = composants.find(c => c.id === parseInt(id));
    setConfig({ ...config, [categorie]: composant });

    // Mise √† jour du prix total
    const newTotal = Object.values({ ...config, [categorie]: composant })
      .reduce((sum, comp) => sum + (comp ? parseFloat(comp.prix) : 0), 0);
    setTotalPrix(newTotal);
  };

  const getCompatibleCartesMeres = () => {
    if (!config.cpu) return [];
    return composants.filter(c => c.categorie === "Carte M√®re" && c.socket === config.cpu.socket);
  };

  return (
    <div className="container">
      <h2 className="text-center my-4">üñ•Ô∏è Configurateur PC</h2>

      <div className="row">
        {["CPU", "Carte M√®re", "RAM", "GPU", "Stockage", "Alimentation", "Bo√Ætier"].map((categorie) => (
          <div className="col-md-4 mb-4" key={categorie}>
            <label className="form-label">{categorie} :</label>
            <select className="form-select" onChange={(e) => handleSelection(categorie.toLowerCase().replace(" ", "_"), e.target.value)}>
              <option value="">S√©lectionner un {categorie}</option>
              {(categorie === "Carte M√®re" ? getCompatibleCartesMeres() : composants.filter(c => c.categorie === categorie)).map(comp => (
                <option key={comp.id} value={comp.id}>{comp.nom} - {comp.prix}‚Ç¨</option>
              ))}
            </select>

            {config[categorie.toLowerCase().replace(" ", "_")] && (
              <div className="card mt-2">
                {config[categorie.toLowerCase().replace(" ", "_")].image_url && (
                  <img src={config[categorie.toLowerCase().replace(" ", "_")].image_url} className="card-img-top" alt={config[categorie.toLowerCase().replace(" ", "_")].nom} />
                )}
                <div className="card-body">
                  <h5 className="card-title">{config[categorie.toLowerCase().replace(" ", "_")].nom}</h5>
                  <p className="card-text">{config[categorie.toLowerCase().replace(" ", "_")].prix}‚Ç¨</p>
                </div>
              </div>
            )}
          </div>
        ))}
      </div>

      <div className="text-center mt-4">
        <h3 className="fw-bold">üí∞ Prix Total : {totalPrix.toFixed(2)}‚Ç¨</h3>
      </div>
    </div>
  );
};

export default Configurator;
