import React, { useEffect, useState } from "react";
import axios from "axios";

const AdminPanel = () => {
  const [nom, setNom] = useState("");
  const [categorie, setCategorie] = useState("");
  const [socket, setSocket] = useState("");
  const [prix, setPrix] = useState("");
  const [composants, setComposants] = useState([]);
  const [selectedComposant, setSelectedComposant] = useState(null);

  useEffect(() => {
    fetchComposants();
  }, []);

  const fetchComposants = () => {
    axios.get("http://127.0.0.1:5000/composants")
      .then((response) => setComposants(response.data))
      .catch((error) => console.error("Erreur :", error));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (selectedComposant) {
      await axios.put(`http://127.0.0.1:5000/composants/${selectedComposant.id}`, {
        nom, categorie, socket, prix
      });
      setSelectedComposant(null);
    } else {
      await axios.post("http://127.0.0.1:5000/composants", { nom, categorie, socket, prix });
    }
    fetchComposants();
    setNom(""); setCategorie(""); setSocket(""); setPrix("");
  };

  const handleEdit = (composant) => {
    setSelectedComposant(composant);
    setNom(composant.nom);
    setCategorie(composant.categorie);
    setSocket(composant.socket);
    setPrix(composant.prix);
  };

  const handleDelete = async (id) => {
    await axios.delete(`http://127.0.0.1:5000/composants/${id}`);
    fetchComposants();
  };

  return (
    <div className="container mt-4">
      <h2>üîß Gestion des Composants</h2>
      <form onSubmit={handleSubmit}>
        <input type="text" placeholder="Nom" value={nom} onChange={(e) => setNom(e.target.value)} required />
        <input type="text" placeholder="Cat√©gorie" value={categorie} onChange={(e) => setCategorie(e.target.value)} required />
        <input type="text" placeholder="Socket" value={socket} onChange={(e) => setSocket(e.target.value)} />
        <input type="number" placeholder="Prix" value={prix} onChange={(e) => setPrix(e.target.value)} required />
        <button type="submit">{selectedComposant ? "Modifier" : "Ajouter"}</button>
      </form>

      <h3>üì¶ Liste des Composants</h3>
      <ul>
        {composants.map((composant) => (
          <li key={composant.id}>
            {composant.nom} ({composant.categorie}) - {composant.prix}‚Ç¨
            <button onClick={() => handleEdit(composant)}>‚úè Modifier</button>
            <button onClick={() => handleDelete(composant.id)}>‚ùå Supprimer</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AdminPanel;
