import React, { useEffect, useState } from "react";
import axios from "axios";

const Configurator = () => {
  const [composants, setComposants] = useState([]);
  const [selectedCpu, setSelectedCpu] = useState(null);
  const [compatibleCartesMeres, setCompatibleCartesMeres] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/composants")
      .then((response) => setComposants(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleCpuSelection = (e) => {
    const cpu = composants.find(c => c.id === parseInt(e.target.value));
    setSelectedCpu(cpu);

    axios.get(`http://127.0.0.1:5000/composants/compatibles?socket=${cpu.socket}&categorie=Carte M√®re`)
      .then((response) => setCompatibleCartesMeres(response.data))
      .catch((error) => console.error("Erreur :", error));
  };

  return (
    <div className="container">
      <h2>üñ•Ô∏è Configurateur PC</h2>

      <label>Processeur :</label>
      <select onChange={handleCpuSelection}>
        <option value="">S√©lectionner un CPU</option>
        {composants.filter(c => c.categorie === "CPU").map((cpu) => (
          <option key={cpu.id} value={cpu.id}>{cpu.nom} ({cpu.socket}) - {cpu.prix}‚Ç¨</option>
        ))}
      </select>

      {selectedCpu && (
        <>
          <h3>üíæ Cartes M√®res Compatibles</h3>
          <select>
            <option value="">S√©lectionner une Carte M√®re</option>
            {compatibleCartesMeres.map((cm) => (
              <option key={cm.id} value={cm.id}>{cm.nom} - {cm.prix}‚Ç¨</option>
            ))}
          </select>
        </>
      )}
    </div>
  );
};

export default Configurator;
