import React, { useEffect, useState } from "react";
import axios from "axios";

const Configurator = () => {
  const [composants, setComposants] = useState([]);
  const [config, setConfig] = useState({});
  const [search, setSearch] = useState("");
  const [totalPrix, setTotalPrix] = useState(0);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/composants")
      .then((response) => setComposants(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleSelection = (categorie, id) => {
    const composant = composants.find(c => c.id === parseInt(id));
    setConfig({ ...config, [categorie]: composant });

    const newTotal = Object.values({ ...config, [categorie]: composant })
      .reduce((sum, comp) => sum + (comp ? parseFloat(comp.prix) : 0), 0);
    setTotalPrix(newTotal);
  };

  return (
    <div className="container">
      <h2 className="text-center my-4">üñ•Ô∏è Configurateur PC</h2>

      <input
        type="text"
        className="form-control mb-3"
        placeholder="Rechercher un composant..."
        value={search}
        onChange={(e) => setSearch(e.target.value)}
      />

      {["CPU", "Carte M√®re", "RAM", "GPU", "Stockage", "Alimentation", "Bo√Ætier"].map((categorie) => (
        <div key={categorie} className="mb-4">
          <h4>{categorie}</h4>

          <div className="row">
            {composants.filter(c => c.categorie === categorie && c.nom.toLowerCase().includes(search.toLowerCase()))
              .map((comp) => (
                <div key={comp.id} className="col-md-4">
                  <div className="card mb-3">
                    {comp.image_url && (
                      <img 
                        src={comp.image_url} 
                        className="card-img-top"
                        alt={comp.nom} 
                        style={{ width: "100%", height: "150px", objectFit: "cover" }}
                      />
                    )}
                    <div className="card-body">
                      <h5 className="card-title">{comp.nom}</h5>
                      <p className="card-text"><strong>Socket :</strong> {comp.socket || "N/A"}</p>
                      <p className="fw-bold text-danger">üí∞ {comp.prix}‚Ç¨</p>
                      <button
                        className="btn btn-primary w-100"
                        onClick={() => handleSelection(categorie.toLowerCase().replace(" ", "_"), comp.id)}
                      >
                        S√©lectionner
                      </button>
                    </div>
                  </div>
                </div>
              ))}
          </div>
        </div>
      ))}

      <div className="text-center mt-4">
        <h3 className="fw-bold">üí∞ Prix Total : {totalPrix.toFixed(2)}‚Ç¨</h3>
      </div>
    </div>
  );
};

export default Configurator;
