import React, { useEffect, useState } from "react";
import axios from "axios";

const Configurator = () => {
  const [composants, setComposants] = useState([]);
  const [config, setConfig] = useState({
    cpu: null,
    carte_mere: null,
    ram: null,
    gpu: null,
    stockage: null,
    alim: null,
    boitier: null
  });

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/composants")
      .then((response) => setComposants(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const handleSelection = (categorie, id) => {
    const composant = composants.find(c => c.id === parseInt(id));

    if (categorie === "cpu") {
      setConfig({ ...config, cpu: composant, carte_mere: null });
    } else if (categorie === "carte_mere") {
      setConfig({ ...config, carte_mere: composant });
    } else if (categorie === "ram") {
      setConfig({ ...config, ram: composant });
    } else if (categorie === "gpu") {
      setConfig({ ...config, gpu: composant });
    } else if (categorie === "stockage") {
      setConfig({ ...config, stockage: composant });
    } else if (categorie === "alim") {
      setConfig({ ...config, alim: composant });
    } else if (categorie === "boitier") {
      setConfig({ ...config, boitier: composant });
    }
  };

  const getCompatibleCartesMeres = () => {
    if (!config.cpu) return [];
    return composants.filter(c => c.categorie === "Carte M√®re" && c.socket === config.cpu.socket);
  };

  return (
    <div className="container">
      <h2>üñ•Ô∏è Configurateur PC</h2>

      <div className="row">
        {/* CPU */}
        <div className="col-md-4">
          <label>Processeur :</label>
          <select onChange={(e) => handleSelection("cpu", e.target.value)}>
            <option value="">S√©lectionner un CPU</option>
            {composants.filter(c => c.categorie === "CPU").map(cpu => (
              <option key={cpu.id} value={cpu.id}>{cpu.nom} ({cpu.socket}) - {cpu.prix}‚Ç¨</option>
            ))}
          </select>
          {config.cpu && <p>‚úÖ {config.cpu.nom}</p>}
        </div>

        {/* Carte M√®re */}
        <div className="col-md-4">
          <label>Carte M√®re :</label>
          <select onChange={(e) => handleSelection("carte_mere", e.target.value)}>
            <option value="">S√©lectionner une Carte M√®re</option>
            {getCompatibleCartesMeres().map(cm => (
              <option key={cm.id} value={cm.id}>{cm.nom} - {cm.prix}‚Ç¨</option>
            ))}
          </select>
          {config.carte_mere && <p>‚úÖ {config.carte_mere.nom}</p>}
        </div>

        {/* RAM */}
        <div className="col-md-4">
          <label>M√©moire RAM :</label>
          <select onChange={(e) => handleSelection("ram", e.target.value)}>
            <option value="">S√©lectionner une RAM</option>
            {composants.filter(c => c.categorie === "RAM").map(ram => (
              <option key={ram.id} value={ram.id}>{ram.nom} - {ram.prix}‚Ç¨</option>
            ))}
          </select>
          {config.ram && <p>‚úÖ {config.ram.nom}</p>}
        </div>

        {/* GPU */}
        <div className="col-md-4">
          <label>Carte Graphique :</label>
          <select onChange={(e) => handleSelection("gpu", e.target.value)}>
            <option value="">S√©lectionner une Carte Graphique</option>
            {composants.filter(c => c.categorie === "GPU").map(gpu => (
              <option key={gpu.id} value={gpu.id}>{gpu.nom} - {gpu.prix}‚Ç¨</option>
            ))}
          </select>
          {config.gpu && <p>‚úÖ {config.gpu.nom}</p>}
        </div>

        {/* Stockage */}
        <div className="col-md-4">
          <label>Stockage :</label>
          <select onChange={(e) => handleSelection("stockage", e.target.value)}>
            <option value="">S√©lectionner un Stockage</option>
            {composants.filter(c => c.categorie === "Stockage").map(storage => (
              <option key={storage.id} value={storage.id}>{storage.nom} - {storage.prix}‚Ç¨</option>
            ))}
          </select>
          {config.stockage && <p>‚úÖ {config.stockage.nom}</p>}
        </div>

        {/* Alimentation */}
        <div className="col-md-4">
          <label>Alimentation :</label>
          <select onChange={(e) => handleSelection("alim", e.target.value)}>
            <option value="">S√©lectionner une Alimentation</option>
            {composants.filter(c => c.categorie === "Alimentation").map(alim => (
              <option key={alim.id} value={alim.id}>{alim.nom} - {alim.prix}‚Ç¨</option>
            ))}
          </select>
          {config.alim && <p>‚úÖ {config.alim.nom}</p>}
        </div>

        {/* Bo√Ætier */}
        <div className="col-md-4">
          <label>Bo√Ætier :</label>
          <select onChange={(e) => handleSelection("boitier", e.target.value)}>
            <option value="">S√©lectionner un Bo√Ætier</option>
            {composants.filter(c => c.categorie === "Bo√Ætier").map(boitier => (
              <option key={boitier.id} value={boitier.id}>{boitier.nom} - {boitier.prix}‚Ç¨</option>
            ))}
          </select>
          {config.boitier && <p>‚úÖ {config.boitier.nom}</p>}
        </div>
      </div>
    </div>
  );
};

export default Configurator;
