import React from "react";

const ComponentCard = ({ composant, addToCart }) => {
  return (
    <div style={{ border: "1px solid #ddd", padding: "10px", margin: "10px", width: "200px" }}>
      <h4>{composant.nom}</h4>
      <p>Catégorie: {composant.categorie}</p>
      <p>Prix: {composant.prix}€</p>
      <button onClick={() => addToCart(composant)}>Ajouter</button>
    </div>
  );
};

export default ComponentCard;
