import React, { useEffect, useState } from "react";
import axios from "axios";
import ComponentCard from "./ComponentCard";
import CartSummary from "./CartSummary";

const Configurator = () => {
  const [composants, setComposants] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    axios.get("http://127.0.0.1:5000/composants")
      .then((response) => setComposants(response.data))
      .catch((error) => console.error("Erreur :", error));
  }, []);

  const addToCart = (composant) => {
    setCart([...cart, composant]);
  };

  return (
    <div>
      <h2>üñ•Ô∏è Configurateur PC</h2>
      <div style={{ display: "flex", flexWrap: "wrap" }}>
        {composants.map((composant) => (
          <ComponentCard key={composant.id} composant={composant} addToCart={addToCart} />
        ))}
      </div>
      <CartSummary cart={cart} />
    </div>
  );
};

export default Configurator;
